            function OnClientclose(radWindow) {
                try {
                    document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDocNumeroDeSerie").focus();
                }
                catch (err) {

                }

            }
            function ReportElectronico() {

                var eval = document.getElementById('ctl00_ContentPlaceHolder1_hfTipImpre').value;
                var HabilitarFactElect = 'NO';
                var vEstadoFactElet = document.getElementById('ctl00_ContentPlaceHolder1_ftEstadoFactElet').value;
                if (eval == "DES") {

                    var tipoDoc = document.getElementById('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnddlTipoDocumento').value;
                    var NroDoc = document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnDocNumeroDeSerie").value + "-" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnDocNumeroDocumento").value
                    var FormaPag = document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaStCred").value;

                    var oWnd = $find("ctl00_ContentPlaceHolder1_RadWindowManager1");
                    var url;

                    if (vEstadoFactElet == "NO") {
                        url = "../../Procesos/Interfaces_Contables/PagoElectronico.aspx?TipDocu=" + tipoDoc + "&NroDocu=" + NroDoc + "&FormaPago=" + FormaPag + "";
                    }
                    else {
                        url = "../../Procesos/Interfaces_Contables/FactElectronicaAvan.aspx?TipDocu=" + tipoDoc + "&NroDocu=" + NroDoc + "&FormaPago=" + FormaPag + "";
                    }



                    oWnd.show();
                    oWnd.Center();
                    oWnd.setUrl(url);
                    //window.open(url, 'yes', 'width=580 height=530 left=10 top=10,  menubar=no,  resizable=no, center: Yes, status=no,  scrollbars=false');
                    return true;

                }
                else
                    return false;

            }

            function Imprimir() {
                if (confirm('¿Usted desea imprimir la Factura/Boleta?')) {
                    if (document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnddlTipoDocumento").value == "NDP") {
                        __doPostBack('ctl00$ContentPlaceHolder1$imgImprimir', '');
                        return;
                    }
                    if (ReportElectronico() == true) {
                        return false;
                    }
                    else {
                        alert("El numero de Serie no esta ")
                        return false;
                    }
                    if (document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnddlTipoDocumento").value != "NDP") {

                        var oWnd = $find("ctl00_ContentPlaceHolder1_RadWindowManager1");

                        oWnd.show();
                        oWnd.Center();
                        oWnd.setUrl("Impresion/ImpFacturasBoletas.aspx?TipoDoc=" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnddlTipoDocumento").value + "&NumSerie=" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnDocNumeroDeSerie").value + "&NumDoc=" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnDocNumeroDocumento").value + "&Salir=SI");
                        //oWnd=radopen("Impresion/ImpFacturasBoletas.aspx?TipoDoc=" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlTipoDocumento").value + "&NumSerie=" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDocNumeroDeSerie").value + "&NumDoc=" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDocNumeroDocumento").value);
                    }
                    else {

                        __doPostBack('ctl00$ContentPlaceHolder1$imgImprimir', '');
                    }


                }
                return false;
            }

            function Imprimir2(tipoDoc, serieDoc, numeroDoc) {
                if (confirm('¿Usted desea imprimir la Factura/Boleta?')) {

