                        //oWnd=radopen("Impresion/ImpFacturasBoletas.aspx?TipoDoc=" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlTipoDocumento").value + "&NumSerie=" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDocNumeroDeSerie").value + "&NumDoc=" + document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDocNumeroDocumento").value);
                    }
                    else {
                        __doPostBack('ctl00$ContentPlaceHolder1$imgImprimir', '');
                    }


                }
                return false;
            }


            function autocompletarDocIni(control) {
                if (control.value != '') {
                    var txt = '000' + control.value;
                    control.value = txt.substr(txt.length - 3, 3);
                    document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbLista_tcCriterioFacturaBoleta_tpRanDoc_txtCriDocIniNum").focus();
                }
            }

            function autocompletarDocIniNum(control) {
                if (control.value != '') {
                    var txt = '00000000' + control.value;
                    control.value = txt.substr(txt.length - 8, 8);
                    document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbLista_tcCriterioFacturaBoleta_tpRanDoc_txtCriDocFin").focus();
                }
            }

            function autocompletarDocFin(control) {
                if (control.value != '') {
                    var txt = '000' + control.value;
                    control.value = txt.substr(txt.length - 3, 3);
                    document.getElementById("ctl00_ContentPlaceHolder1_tcFacturacion_tbLista_tcCriterioFacturaBoleta_tpRanDoc_txtCriDocFinNum").focus();
                }
            }

            function autocompletarDocFinNum(control) {
                if (control.value != '') {
                    var txt = '00000000' + control.value;
                    control.value = txt.substr(txt.length - 8, 8);

                    $get('ctl00_ContentPlaceHolder1_hidAutorizacion').value = control.value;
                    __doPostBack('ctl00$ContentPlaceHolder1$hidAutorizacion', '');
                }
            }

            document.onkeydown = keyEventListener;
            document.onkeypress = keyEventListener;

            function textFormat(evt, caja) {
                var charCode = (evt.which) ? evt.which : window.event.keyCode;
                var texto = caja.value;
                if (charCode != 37 && charCode != 39 && charCode != 9 && charCode != 16) {
                    var regex = new RegExp("[^\A-Za-z0-9.;,:#$%&* \\(\\)\\/\\¿\\?\\!\\¡\\-\\\\]", "g");
                    var nTexto = texto.replace(regex, "");
                    if (nTexto != caja.value) {
                        caja.value = nTexto;
                    }
                }
            }

            function keyEventListener(evt) {
                var keycode;
                if (window.event) keycode = window.event.keyCode;
                else if (e) keycode = e.which;
                var e = e || window.event;
                if (keycode == 50 && e.altKey) {
                    //alert('Alt + 2: Grabar');
                    if (confirm('¿Usted desea insertar la Factura?'))
                        __doPostBack('ctl00$ContentPlaceHolder1$imgbtnAdicionar', '');
                }
                if (keycode == 51 && e.altKey) {
                    //alert('Alt + 3: Actualizar');
                    if (confirm('¿Usted desea modificar la Factura?'))
                        __doPostBack('ctl00$ContentPlaceHolder1$imgbtnModificar', '');
                }
                if (keycode == 52 && e.altKey) {
                    //alert('Alt + 4: Cambia Pestaña');
                    var tab = $find('ctl00_ContentPlaceHolder1_tcFacturacion');
                    tab.set_activeTab(tab.get_tabs()[1]);
                }
                if (keycode == 54 && e.altKey) {
                    //alert('Alt + 6: Eliminar');
                    if (confirm('¿Usted desea eliminar la Factura?'))
                        __doPostBack('ctl00$ContentPlaceHolder1$imgbtnEliminar', '');
                }
                if (keycode == 55 && e.altKey) {
                    //alert('Alt + 7: Limpiar');
                    __doPostBack('ctl00$ContentPlaceHolder1$imgbtnLimpia', '');
                }
                if (keycode == 56 && e.altKey) {
                    //alert('Alt + 8: Imprimir');
                    var rtn;
                    rtn = Imprimir()
                }
            }


            function CrearGrilla(sender, args) {
                var grid = sender;
                var index = $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_tbIndex').value

                if (index == "")
                    index = 0;
                else
                    index = parseInt(index);

                if (index >= 0 && index < grid.get_masterTableView().get_dataItems().length) {
                    grid.set_activeRow(grid.get_masterTableView().get_dataItems()[index].get_element());
                    var masterTable = grid.get_masterTableView();
                    masterTable.clearSelectedItems();
                    masterTable.selectItem(masterTable.get_dataItems()[index].get_element());
                    grid.set_activeRow(grid.get_masterTableView().get_dataItems()[index].get_element());
                    //$get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_rgDetalleFactura').focus();
                }
                else {
                    if (index >= 0 && grid.get_masterTableView().get_dataItems().length > 0) {
                        index = 0;
                        grid.set_activeRow(grid.get_masterTableView().get_dataItems()[index].get_element());
                        var masterTable = grid.get_masterTableView();
                        masterTable.clearSelectedItems();
                        masterTable.selectItem(masterTable.get_dataItems()[index].get_element());
                        grid.set_activeRow(grid.get_masterTableView().get_dataItems()[index].get_element());
                        //$get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_rgDetalleFactura').focus();
                    }
                }
            }

            function RowSelected(sender, eventArgs) {
                $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_tbIndex').value = eventArgs._itemIndexHierarchical;
            }

            function onKeyPress(sender, args) {
                if (args.get_keyCode() != 13 && !(args.get_isShiftPressed() && args.get_keyCode() == 68) && !(args.get_isShiftPressed() && args.get_keyCode() == 100))
                    return;

                var el = Telerik.Web.UI.Grid.GetCurrentElement(args.get_domEvent());

                if ((args.get_isShiftPressed() && args.get_keyCode() == 68) || (args.get_isShiftPressed() && args.get_keyCode() == 100)) { // Shift-D
                    var grid = $find('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_rgDetalleFactura');
                    var masterTable = grid.get_masterTableView();
                    if (masterTable.get_selectedItems().length > 0) {
                        var fila = masterTable.get_selectedItems()[0].get_element();
                        var rowIndex = fila.rowIndex;
                        sender.get_masterTableView().deleteItem(fila);
                    }
                    return;
                }

                if ($(el).parents("tr").attr("id") == "") {
                    if (args.get_keyCode() == 13) {
                        sender.get_masterTableView().insertItem($(el).parents("tr")[0]);
                    }
                }
                else if ($(el).parents("tr").attr("id") && $(el).parents("tr").attr("id") != "") {
                    if (args.get_keyCode() == 13)
                        sender.get_masterTableView().updateItem($(el).parents("tr")[0]);
                }

            }

            function CancelKeyPressedEnter(sender, eventArgs) {
                if (eventArgs.get_keyCode() == 13) {
                    eventArgs.set_cancel(true)
                }
            }

            function DobleClickFactura(sender, eventArgs) {
                var tab = $find('ctl00_ContentPlaceHolder1_tcFacturacion');
                tab.set_activeTab(tab.get_tabs()[0]);
                var MasterTable = sender.get_masterTableView();
                var row = MasterTable.get_dataItems()[eventArgs.get_itemIndexHierarchical()];
                var numDocu = MasterTable.getCellByColumnUniqueName(row, "NU_DOCU").innerHTML;
                var tiDocu = MasterTable.getCellByColumnUniqueName(row, "TI_DOCU").innerHTML;

                $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDocNumeroDeSerie').value = numDocu.split("-")[0];
                $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDocNumeroDocumento').value = numDocu.split("-")[1];
                $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlTipoDocumento').value = tiDocu;

                $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnDocNumeroDeSerie').value = numDocu.split("-")[0];
                $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnDocNumeroDocumento').value = numDocu.split("-")[1];
                $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_hdnddlTipoDocumento').value = tiDocu;


                $get('ctl00_ContentPlaceHolder1_hidSelectNum').value = numDocu.split("-")[0] + '-' + numDocu.split("-")[1];
                $get('ctl00_ContentPlaceHolder1_hidSelectTipo').value = tiDocu;

                __doPostBack("ctl00$ContentPlaceHolder1$hidSelectNum", '');
            }

            function CambiarTipoDocumento(Serie) {
                var opcion = $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlTipoDocumento').value;

                if (opcion == "FAC") {
                    $get(Serie).value = "";
                }
                else {
                    if (opcion == "BOL") {
                        $get(Serie).value = "002";
                    }
                    else {
                        if (opcion == "NDP") {
                            $get(Serie).value = "999";
                        }
                    }
                }
            }

            function CambiarFormaPago(Banco, MonedaCobranza, descripcion) {
                var opcion = $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaStCred').value;
                $get(descripcion).value = "";

                if (opcion == "Contado") {
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDiasVcto').className = "txtboxreadonly";
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDiasVcto').disabled = true;
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDiasVcto').value = "0";

                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaEfectivoCheque').selectedIndex = 1;
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaEfectivoCheque').disabled = false;
                    //                    $get(MonedaCobranza).className = "";
                    //                    $get(MonedaCobranza).disabled = false;
                    //                    $get(MonedaCobranza).value = "";

                    $get(Banco).className = "";
                    $get(Banco).disabled = false;
                    $get(Banco).value = "";

                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').className = "";
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').disabled = false;
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').value = "";

                    CambiarEfectivoCheque(Banco, descripcion);
                }
                else
                    if (opcion == "Credito") {
                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDiasVcto').className = "";
                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDiasVcto').disabled = false;
                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtDiasVcto').value = "8";

                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaEfectivoCheque').selectedIndex = 1;
                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaEfectivoCheque').disabled = false;

                        //                    $get(MonedaCobranza).className = "txtboxreadonly";
                        //                    $get(MonedaCobranza).disabled = true;
                        //                    $get(MonedaCobranza).value = "";

                        $get(Banco).className = "txtboxreadonly";
                        $get(Banco).disabled = true;
                        $get(Banco).value = "";

                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').className = "";
                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').disabled = false;
                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').value = "";
                    }
            }

            function CambiarEfectivoCheque(Banco, descripcion) {

                $get(Banco).value = "";
                $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').value = "";
                $get(descripcion).value = "";

                if ($get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaEfectivoCheque').selectedIndex == 0) {

                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').className = "";
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').disabled = false;
                    $get(Banco).className = "";
                    $get(Banco).disabled = false;

                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtNroCuenta').className = "";
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtNroCuenta').disabled = false;
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtNroCuenta').value = "";
                }

                if ($get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaEfectivoCheque').selectedIndex == 1) {

                    $get(Banco).className = "txtboxreadonly";
                    $get(Banco).disabled = true;
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').className = "txtboxreadonly";
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').disabled = true;

                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtNroCuenta').className = "txtboxreadonly";
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtNroCuenta').disabled = true;
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtNroCuenta').value = "";
                }


                if ($get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaEfectivoCheque').selectedIndex == 2) {

                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').className = "txtboxreadonly";
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').disabled = true;

                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtNroCuenta').className = "";
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtNroCuenta').disabled = false;
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtNroCuenta').value = "";

                    $get(Banco).className = "";
                    $get(Banco).disabled = false;
                }
                return;

                var opcion = $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_ddlCobranzaEfectivoCheque').value;
                $get(descripcion).value = "";

                if (opcion == "N") { // Cheque
                    $get(Banco).className = "";
                    $get(Banco).disabled = false;
                    $get(Banco).value = "";

                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').className = "";
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').disabled = false;
                    $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').value = "";
                }
                else
                    if (opcion == "S") { //Efectivo                
                        $get(Banco).className = "txtboxreadonly";
                        $get(Banco).disabled = true;
                        $get(Banco).value = "";

                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').className = "txtboxreadonly";
                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').disabled = true;
                        $get('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_txtChequeNum').value = "";
                    }

            }

