            function Fecha() {
                Result = true;
                if (!validarRadFecha($find("ctl00_ContentPlaceHolder1_tcFacturacion_tbLista_tcCriterioFacturaBoleta_tpRanFechEmi_txtCritFechaInicial"))) {
                    Result = false;
                    alert('Ingrese Correctamente Fecha Inicio!!!.');
                }
                if (!validarRadFecha($find("ctl00_ContentPlaceHolder1_tcFacturacion_tbLista_tcCriterioFacturaBoleta_tpRanFechEmi_txtCritFechaFinal"))) {
                    Result = false;
                    alert('Ingrese Correctamente Fecha Fin!!!.');
                }
                if (Result == true) {
                    if (!compararradFechas($find("ctl00_ContentPlaceHolder1_tcFacturacion_tbLista_tcCriterioFacturaBoleta_tpRanFechEmi_txtCritFechaInicial"), $find("ctl00_ContentPlaceHolder1_tcFacturacion_tbLista_tcCriterioFacturaBoleta_tpRanFechEmi_txtCritFechaFinal"))) {
                        Result = false;
                        alert('Fecha de Inicio no puede ser mayor que la Fecha de Fin!!!.');
                    }
                }
                return Result;
            }

            function validarRadFecha(rdControl) {
                if (rdControl.isEmpty()) {
                    return false;
                }
                return true;
            }

            function compararradFechas(rdControlIni, rdControlFin) {
                var fecha1 = rdControlIni.get_selectedDate();
                mes1 = fecha1.getMonth() + 1

                if (mes1.length == 1) {
                    mes1 = '0' + mes1;
                }

                var myDate1 = new Date();
                myDate1.setFullYear(fecha1.getFullYear(), mes1, fecha1.getDate());

                var fecha2 = rdControlFin.get_selectedDate();
                mes2 = fecha2.getMonth() + 1
                if (mes2.length == 1) {
                    mes2 = '0' + mes2;
                }

                var myDate2 = new Date();
                myDate2.setFullYear(fecha2.getFullYear(), mes2, fecha2.getDate());

                if (myDate1 <= myDate2) {
                    return true;
                }
                else {
                    return false;
                }
            }
            function ReImprimir(NU_DOCU, TI_DOCU, FO_PAGO) {
                if (confirm('¿Usted desea imprimir la Factura/Boleta?')) {
                    ReportElect(NU_DOCU, TI_DOCU, FO_PAGO);
                }
            }
            function ReportElect(NU_DOCU, TI_DOCU, FO_PAGO) {

                var tipoDoc = TI_DOCU;
                var NroDoc = NU_DOCU;
                var FormaPag = FO_PAGO;

                var oWnd = $find("ctl00_ContentPlaceHolder1_RadWindowManager1");
                var url = "../../Procesos/Interfaces_Contables/FactElectronicaAvan.aspx?TipDocu=" + tipoDoc + "&NroDocu=" + NroDoc + "&FormaPago=" + FormaPag + "";


                oWnd.show();
                oWnd.Center();
                oWnd.setUrl(url);

                return true;




            }








            function Buscar() {
                var tab = $find('ctl00_ContentPlaceHolder1_tcFacturacion');
                tab.set_activeTab(tab.get_tabs()[1]);
            }

            function OpenWindow() {
                Sys.Application.add_load(ow);
            }

            function ow() {
                var oUrl = document.getElementById("ctl00_ContentPlaceHolder1_hdUrl").value;
                var oWnd = radopen(oUrl, 'rwReporte');
                oWnd.maximize();
                oWnd.add_close(OnClientClose);
                Sys.Application.remove_load(ow);
            }

            function OnClientClose(oWnd, eventArgs) {
                oWnd.remove_close(OnClientClose);
            }

