                    var myOffsetParent = highlighted.offsetParent;
                    while( myOffsetParent )
                    {
                        offset += myOffsetParent.offsetTop;
                        myOffsetParent = myOffsetParent.offsetParent;
                    }
                    window.scrollTo( 0, offset);
                }
