td.script:
    function OnClientclosePostBack() {
        __doPostBack(destino, '');
    }

    var ctrlCodigo;
    var ctrlDescri;
    var funcionBus;

    function FunctionChanged() {
        ctrlCodigo = arguments[0].id;
        ctrlDescri = arguments[1].id;
        funcionBus = arguments[2];
         
        cargardato()
    }

    function cargardato() {
        var Codigo = autoCompletarTextBox(ctrlCodigo);
        var Descri = '';
        var FuncionBusqueda = funcionBus;
         
        TALMA.Web.Importacion.ObtenerDatos.ObtenerConsignatario(FuncionBusqueda, '02', Codigo, Descri, true, true, updateCaja);
    }

    function updateCaja(result) {
        
        if (result == null || result == '') {
            document.getElementById(ctrlCodigo).value = '';
            document.getElementById(ctrlDescri).value = '';
            document.getElementById('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_mpbTipoBanco_hdnFoco').value = '0';
        }
        else {
            document.getElementById(ctrlDescri).value = result;
            document.getElementById('ctl00_ContentPlaceHolder1_tcFacturacion_tbDatosGenerales_mpbTipoBanco_hdnFoco').value = '1';
        }
    }

    function autoCompletarTextBox(oCtrlCodigo) {
        var TamanioCaja = $get(oCtrlCodigo).maxLength;
        var ValorCaja = $get(oCtrlCodigo).value.toUpperCase();
        var TamanioValor = String(ValorCaja).length;
        var autoCompletar = '1';
        
        if (TamanioValor < TamanioCaja && autoCompletar == '1') {
            var sizeZeros = TamanioCaja - TamanioValor;
            for (i = 0; i < sizeZeros ; i++) {
                ValorCaja = '0' + ValorCaja
            }
            
            $get(oCtrlCodigo).value = ValorCaja; 
        }
        
        return ValorCaja; 
    }

