var oMessageContent;
var oMessageBackground;
var MoisheMessage;
var vType;
var MoisheFocusReturn = '';
function msgShowClient() {
    vType = 1;
    MoisheMessage = '';
    for (var x = 0; x < arguments.length; x++) {      msgAddMessage(arguments[x]);
    }
    msgShow();
}
function msgShowServer() {
    var vHeight;
    vType = 1;
    oMessageBackground = document.getElementById('ctl00_msgBackgroundPanel');
    oMessageContent = document.getElementById('ctl00_msgContentPanel');
    oMessageContent.style.display = 'block';
    oMessageBackground.style.display = 'block';
    msgSetBounds();
    msgSetType();
    msgSetBackground();
    window.onscroll = msgSetBounds;
}
function msgShow() {
    var vHeight;
    var oMessageSpan;
    oMessageSpan = document.getElementById('MessageSpan');
    oMessageBackground = document.getElementById('ctl00_msgBackgroundPanel');
    oMessageContent = document.getElementById('ctl00_msgContentPanel');
    oMessageSpan.innerHTML = MoisheMessage;
    oMessageContent.style.display = 'block';
    oMessageBackground.style.display = 'block';
    msgSetBounds();
    msgSetType();
    msgSetBackground();
    window.onscroll = msgSetBounds;
}
function msgSetType() {
    if (vType == 1) {
        document.getElementById('ctl00_OKButton').style.display = 'block';
        document.getElementById('ctl00_YesButton').style.display = 'none';
        document.getElementById('ctl00_NoButton').style.display = 'none';
        document.getElementById('ctl00_OKButton').focus();
    }
    if (vType == 2) {
        document.getElementById('ctl00_OKButton').style.display = 'none';
        document.getElementById('ctl00_YesButton').style.display = 'block';
        document.getElementById('ctl00_NoButton').style.display = 'block';
        document.getElementById('ctl00_YesButton').focus();
    }
}
function msgSetBackground() {
    oMessageBackground = document.getElementById('ctl00_msgBackgroundPanel');
    oMessageBackground.style.width = document.documentElement.clientWidth;
    oMessageBackground.style.height = document.documentElement.clientHeight;
}
function msgSetBounds() {
    oMessageContent = document.getElementById('ctl00_msgContentPanel');
    vTop = document.documentElement.scrollTop;
    vLeft = document.documentElement.scrollLeft;
    oMessageContent.style.top = vTop + ((document.documentElement.clientHeight/2)-(oMessageContent.style.height/2));
}
function msgHidden() {
    oMessageBackground = document.getElementById('ctl00_msgBackgroundPanel');
    oMessageContent = document.getElementById('ctl00_msgContentPanel');
    oMessageBackground.style.display = 'none';
    oMessageContent.style.display = 'none';
    if(MoisheFocusReturn != '')
       document.getElementById(MoisheFocusReturn).focus();
}
function msgAddMessage(strMessage)
{
    MoisheMessage = MoisheMessage + '&#8226; ' + strMessage + ' <br>';
}
function MoisheKeyDownButtonMove(sender) {
   if (event.keyCode == 37 && sender.value != 'Aceptar')
    document.getElementById('ctl00_YesButton').focus();
   if (event.keyCode == 39 && sender.value != 'Aceptar')
    document.getElementById('ctl00_NoButton').focus();
   if (event.keyCode == 13) {
      if (sender.value == 'Si')
          MoisheDoPostBack();
      if (sender.value == 'No' || sender.value == 'Aceptar')
          msgHidden();
   }
   if (event.keyCode == 27) {
       msgHidden();
    }
}
